function Auth(){this.maskwapper=$(".mask-wapper"),this.scollwapper=$(".scoll-wapper")}Auth.prototype.run=function(){var t=this;t.listenshowhideEvent(),t.listenSwitchEvent(),t.listenSigninEvent()},Auth.prototype.showEvent=function(){this.maskwapper.show()},Auth.prototype.hideEvent=function(){this.maskwapper.hide()},Auth.prototype.listenshowhideEvent=function(){var t=this,n=$(".editor-btn"),e=$(".delete-btn"),i=$(".close-btn");n.click(function(){t.showEvent()}),e.click(function(){t.showEvent()}),i.click(function(){t.hideEvent()})},Auth.prototype.listenSwitchEvent=function(){var n=this;$(".switch").click(function(){var t=n.scollwapper.css("left");(t=parseInt(t))<0?n.scollwapper.animate({left:"0"}):n.scollwapper.animate({left:"-400px"})})},Auth.prototype.listenSigninEvent=function(){var o=this,t=$(".signin-group"),i=t.find("input[name='MobilePhone']"),s=t.find("input[name='password']"),c=t.find("input[name='remember']");t.find(".submit-btn").click(function(){var t=i.val(),n=s.val(),e=c.prop("checked");xfzajax.post({url:"/account/login/",data:{MobilePhone:t,password:n,remember:e?1:0},success:function(t){if(200==t.code)o.hideEvent(),window.location.reload();else{var n=t.message;if("string"==typeof n||n.constructor==String)window.messageBox.show(n);else for(var e in n){var i=n[e][0];window.messageBox.show(i)}}},fail:function(t){console.log(t)}})})},$(function(){(new Auth).run()});